mapper.insert:
INSERT INTO login_user ( id, user_name, account, password, phone, permission, register_time, last_online_time , avatar_file_id
) VALUES ( #{id}, #{userName}, #{account}, #{password}, #{phone}, #{permission}, #{registerTime}, #{lastOnlineTime} , #{avatarFileId}
)

mapper.update:
UPDATE login_user
SET last_online_time = #{lastOnlineTime}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM login_user
WHERE account = #{account}

SELECT id
FROM login_user
WHERE account = #{account}
